from collections import namedtupleclass Stack:        ElementWithCachedMax = namedtuple('ElementWithCachedMax',('element', 'max'))            def __init__(self):        self._element_with_cached_max = []                def empty(self):        return len(self._element_with_cached_max) == 0            def max(self):        return self._element_with_cached_max[-1].max        def push(self, x):        self._element_with_cached_max.append( \                  self.ElementWithCachedMax(x, x if self.empty() \                                       else max(self.max(),x)))    def pop(self):        if self.empty():            raise IndexError('pop() : stack empty')        return self._element_with_cached_max.pop().element    